import{getAllBooks}from"./api.min.js";import{render}from"./render.min.js";const searchResults=document.getElementById("searchResults");async function search(e){const t=e.target.value.toLowerCase();if(""===t)searchResults.innerHTML="";else{const s=await getAllBooks();e=s.filter(e=>e.name.toLowerCase().includes(t));render(e,t)}}function debounce(t,s=200){let r;return(...e)=>{clearTimeout(r),r=setTimeout(()=>{t.apply(this,e)},s)}}export{search,debounce};